mapper.select:
select count(1)
from subject_info a,
     subject_mapping b
where
    a.id = b.subject_id
and b.category_id = #{categoryId}
and b.label_id = #{labelId}
and a.is_deleted = 0
and b.is_deleted = 0

    and a.subject_difficult = #{subjectInfo.subjectDifficult}


    and a.subject_type = #{subjectInfo.subjectType}

select a.id, a.subject_name, a.subject_difficult, a.settle_name, a.subject_type, a.subject_score, a.subject_parse, a.created_by, a.created_time, a.update_by, a.update_time, a.is_deleted from subject_info a, subject_mapping b where a.id = b.subject_id and b.category_id = #{categoryId} and b.label_id = #{labelId} and a.is_deleted = 0 and b.is_deleted = 0
and a.subject_difficult = #{subjectInfo.subjectDifficult}
and a.subject_type = #{subjectInfo.subjectType}
limit #{start}, #{pageSize}

select
  id, subject_name, subject_difficult, settle_name, subject_type, subject_score, subject_parse, created_by, created_time, update_by, update_time, is_deleted
from subject_info


        and id = #{id}


        and subject_name = #{subjectName}


        and subject_difficult = #{subjectDifficult}


        and settle_name = #{settleName}


        and subject_type = #{subjectType}


        and subject_score = #{subjectScore}


        and subject_parse = #{subjectParse}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from subject_info


        and id = #{id}


        and subject_name = #{subjectName}


        and subject_difficult = #{subjectDifficult}


        and settle_name = #{settleName}


        and subject_type = #{subjectType}


        and subject_score = #{subjectScore}


        and subject_parse = #{subjectParse}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}
